import styled from "styled-components";

export const AuthPage = styled.div`
  width: 100%;
  height: 100vh;
  background-color: #1A202C;
  display: flex;
  align-items: center;
  justify-content: center; 
   
  .card{
    width: 35%;
    height: 70%;
    background-color: #262f42;
    border-radius: 10px;
    display: flex;
    align-items: center;
    justify-content: space-evenly;
    flex-direction: column;
    padding:  20px;
    gap: 20px;
    
    img{
      width: 150px;
    }
    form{
      width: 80%;
      display: flex;
      flex-direction: column;
      gap: 15px;
    }
  }
`
