import { Button, Input , Upload, Modal } from 'antd';
const App = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const showModal = () => {
    setIsModalOpen(true);
  };
  const handleOk = () => {
    setIsModalOpen(false);
  };
  const handleCancel = () => {
    setIsModalOpen(false);
  };
  return (
    <>
      <div className='button'>
      <Button style={{padding:"20px",display:"flex",alignItems:"center",background:"#FEC200"}} type="primary" onClick={showModal}>
             + Add Course
      </Button>
      <Modal title="Add Course" style={{lineHeight:"30px"}} open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>
      <Upload
        action="https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188"
        listType="picture-circle"
        fileList={fileList}
        onPreview={handlePreview}
        onChange={handleChange}
      >
        {fileList.length >= 8 ? null : uploadButton}
      </Upload>
        <Input placeholder='Course Name'/>
        <Input placeholder='Price'/>
        <Input placeholder='Course Name'/>

      </Modal>

      </div>
    </>
  );
};
export default App;
