import React, {useEffect} from 'react';
import {Button, Input, Switch, Table} from 'antd';
import {useDispatch, useSelector} from "react-redux";
import AddModal from "./addModal";
import {deleteWebinar, editWebinar, getWebinars} from "../../../redux/actions/app";
import {DeleteOutlined, EditOutlined} from "@ant-design/icons";
import {updateState} from "../../../redux/reducers";

function DrawerPage() {
    const {webinar} = useSelector(state => state.app);
    const dispatch = useDispatch();
    useEffect(()=>{
        dispatch(getWebinars());
    },[])
    console.log(webinar)
    const columns = [
        {
            title: 'N',
            render: (a,b,index) => index +1
        },
        {
            title: 'Webinar nomi',
            dataIndex: 'title'
        },
        {
            title: 'Webinar link',
            dataIndex: 'link'
        },
        {
            title: 'Malumot',
            dataIndex: 'description'
        },
        {
            title: 'Holat',
            render:(item) => <Switch onChange={(status) => dispatch(editWebinar({...item,status})) } checked={item?.status}/>
        },
        {
            align: 'center',
            render:(e)=> <div className={'actions'}>
                <Button onClick={()=> dispatch(updateState({isModal:true, editData:e})) } style={{padding: 0}} type={'primary'} > <EditOutlined /> </Button>
                <Button style={{padding: 0}}  type={'primary'} onClick={()=> dispatch(deleteWebinar(e.id)) } danger > <DeleteOutlined /> </Button>
            </div>
        }
    ]

    return (
       <div>
           <div className={'Header'}>
               <h2 className={'titleColor'}> Webinarlar </h2>
               <div className="actions">
                   <Input rootClassName={'search'} placeholder="Qidiruv"/>
                   <AddModal/>
               </div>
           </div>
           <Table columns={columns} dataSource={webinar}> </Table>
       </div>
    );
}

export default DrawerPage;
