import React, { useState } from 'react';
import { Button, Input, Upload, Modal, Card } from 'antd'; // Added Card component
import { PlusOutlined } from "@ant-design/icons";

const getBase64 = (file) =>
  new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });

const App = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [courseDetails, setCourseDetails] = useState({ name: '', price: '', description: '' }); // Added state for course details
  const [courseList, setCourseList] = useState([]); // State to hold added courses

  const handleOk = () => {
    setCourseList([...courseList, courseDetails]); // Add the new course to the course list
    setCourseDetails({ name: '', price: '', description: '' }); // Reset the course details state
    setIsModalOpen(false);
  };

  // ...Other state and functions remain unchanged

  const updateCourseDetail = (key, value) => {
    setCourseDetails({ ...courseDetails, [key]: value }); // Update course details for specific key
  };

  return (
    <>
      <div className='button'>
        <Button style={{ padding: "20px", display: "flex", alignItems: "center", background: "#FEC200" }} type="primary" onClick={() => setIsModalOpen(true)}>
          + Add Course
        </Button>

        <Modal title="Add Course" style={{ lineHeight: "30px" }} open={isModalOpen} onOk={handleOk} onCancel={() => setIsModalOpen(false)}>
          <Upload /* ...other props */>
          {/* ... */}
          </Upload>

          <Input
            style={{ padding: "10px" }}
            placeholder='Course Name'
            value={courseDetails.name}
            onChange={(e) => updateCourseDetail('name', e.target.value)}
          />
          <Input
            style={{ padding: "10px", margin: "10px 0" }}
            placeholder='Price'
            value={courseDetails.price}
            onChange={(e) => updateCourseDetail('price', e.target.value)}
          />
          <Input
            style={{ padding: "10px" }}
            placeholder='Description'
            value={courseDetails.description}
            onChange={(e) => updateCourseDetail('description', e.target.value)}
          />
        </Modal>
        
        <div style={{ marginTop: '20px' }}>
          {courseList.map((course, index) => (
            <Card key={index} title={course.name} style={{ width: 300, marginTop: '10px' }}>
              <p>Price: {course.price}</p>
              <p>Description: {course.description}</p>
            </Card>
          ))}
        </div>

      </div>
    </>
  );
};

export default App;
