import React, { useState } from 'react';
import { Button, Input , Upload, Modal } from 'antd';
import {PlusOutlined} from "@ant-design/icons"
const getBase64 = (file) =>
  new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });

const App = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const showModal = () => {
    setIsModalOpen(true);
  };
  const handleOk = () => {
    setIsModalOpen(false);
  };
  const handleCancel = () => {
    setIsModalOpen(false);
  };
  const [previewOpen, setPreviewOpen] = useState(false);
  const [previewImage, setPreviewImage] = useState('');
  const handleCancel1 = () => setPreviewOpen(false);
  const [previewTitle, setPreviewTitle] = useState('');
  const [fileList, setFileList] = useState([]);
  const [courseName, setCourseName] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');

  const handlePreview = async (file) => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj);
    }
    setPreviewImage(file.url || file.preview);
    setPreviewOpen(true);
    setPreviewTitle(file.name || file.url.substring(file.url.lastIndexOf('/') + 1));
  };
  const handleChange = ({ fileList: newFileList }) => setFileList(newFileList);
  const uploadButton = (
    <button
      style={{
        border: 0,
        background: 'none',
      }}
      type="button"
    >
      <PlusOutlined />
      <div
        style={{
          marginTop: 8,
        }}
      >
        Upload
      </div>
    </button>
  );
  return (
    <>
      <div className='button'>
      <Button style={{padding:"20px",display:"flex",alignItems:"center",background:"#FEC200"}} type="primary" onClick={showModal}>
             + Add Course
      </Button>
      <Modal title="Add Course" style={{lineHeight:"30px"}} open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>
      <Upload
        action="https://run.mocky.io/v3/435e224c-44fb-4773-9faf-380c5e6a2188"
        listType="picture-circle"
        fileList={fileList}
        onPreview={handlePreview}
        onChange={handleChange}
      >
        {fileList.length >= 8 ? null : uploadButton}
      </Upload>
        <Input style={{padding:"10px"}} placeholder='Course Name'  value={courseName} onChange={(e) => setCourseName(e.target.value)}/>
        <Input style={{padding:"10px",margin:"10px 0px"}}  placeholder='Price' value={price} onChange={(e) => setPrice(e.target.value)}/>
        <Input style={{padding:"10px"}} placeholder='Descrption' value={description} onChange={(e) => setDescription(e.target.value)}/>

      </Modal>

      </div>
    </>
  );
};
export default App;
