import React, { useState } from 'react';
import { Card, Input, Button } from 'antd';
import 'antd/dist/antd.css';

const { TextArea } = Input;
const { Meta } = Card;

const BlogPostPage = () => {
  const [blogPosts, setBlogPosts] = useState([]);
  const [blogPostInfo, setBlogPostInfo] = useState({
    title: '',
    content: '',
    imageUrl: ''
  });

  // Handles input changes for all fields
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBlogPostInfo({ ...blogPostInfo, [name]: value });
  };

  // Saves the blog post to the list of posts
  const addBlogPost = () => {
    if (!blogPostInfo.title || !blogPostInfo.content) {
      alert("Please fill in the title and content.");
      return;
    }
    setBlogPosts([...blogPosts, blogPostInfo]);
    setBlogPostInfo({ title: '', content: '', imageUrl: '' }); // Reset after submit
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Create a Blog Post</h2>
      <Input
        placeholder="Title"
        style={{ marginBottom: '10px' }}
        value={blogPostInfo.title}
        name="title"
        onChange={handleInputChange}
      />
      <TextArea
        placeholder="Content"
        rows={4}
        style={{ marginBottom: '10px' }}
        value={blogPostInfo.content}
        name="content"
        onChange={handleInputChange}
      />
      <Input
        placeholder="Image URL (optional)"
        style={{ marginBottom: '10px' }}
        value={blogPostInfo.imageUrl}
        name="imageUrl"
        onChange={handleInputChange}
      />
      <Button type="primary" onClick={addBlogPost}>
        Submit Blog Post
      </Button>
      <div style={{ marginTop: '20px' }}>
        {blogPosts.map((post, index) => (
          <Card
            key={index}
            hoverable
            style={{ width: 240, marginTop: '16px' }}
            cover={post.imageUrl && <img alt="example" src={post.imageUrl} />}
          >
            <Meta title={post.title} description={post.content} />
          </Card>
        ))}
      </div>
    </div>
  );
};

export default BlogPostPage;
