import React, {useEffect} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {deleteEmployee, editEmployee, getTeams} from '../../../redux/actions/app';
import AddTeam from "./addTeam";
import {Button, Popconfirm, Switch, Table} from "antd";
import {updateState} from "../../../redux/reducers";
import {DeleteOutlined, EditOutlined} from "@ant-design/icons";

const Teams = () => {
    const {teams} = useSelector(state => state.app);
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(getTeams());
    }, []);

    const confirm = (e) => {
        dispatch(deleteEmployee(e.id));
    };

    const columns = [
        {
            title:'N',
            render:(a,b,index) => index+1
        },
        {
            title: 'FISH',
            render:(item)=> <div className={'courseImg'}>
                <img src={item?.image} alt="no-img"/>
                <span>{item?.title}</span>
            </div>
        },
        {
            title: 'Lavozim',
            dataIndex: 'position',
        },
        {
            title: "Ma'lumot",
            dataIndex: "description",
        },
        {
            title: 'Holat',
            render:(e)=> <Switch onChange={(status)=> dispatch(editEmployee({...e,status})) } checked={e?.status} />
        },
        {

            render:(e)=> <div className={'actions'}>
                <Button onClick={()=>dispatch(updateState({isModal:true, editData:e}))} style={{padding: 0}} type={'primary'} > <EditOutlined /> </Button>
                <Popconfirm
                    title="Delete the task"
                    description="Are you sure to delete this task?"
                    onConfirm={()=>confirm(e)}
                    okText="Yes"
                    cancelText="No"
                    placement="leftTop"
                >
                    <Button  style={{padding: 0}}  type={'primary'} danger > <DeleteOutlined /> </Button>
                </Popconfirm>
            </div>
        }
    ]

    return (
        <div>
            <div className="Header">
                <h2 className={'titleColor'}> Xodimlar </h2>
                <div className="actions">
                    <AddTeam/>
                </div>
            </div>
            <Table columns={columns} dataSource={teams} > </Table>
        </div>
    );
}

export default Teams;
