import React, { useState } from 'react';
import { Button, Input, Upload, Modal, Card } from 'antd';
import { PlusOutlined } from "@ant-design/icons";

const getBase64 = (file) =>
  new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });

const App = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  // Updated state to include the image base64 string
  const [courseDetails, setCourseDetails] = useState({
    name: '',
    price: '',
    description: '',
    image: ''
  });
  const [courseList, setCourseList] = useState([]);

  const handleOk = () => {
    if (courseDetails.image) {
      setCourseList([...courseList, courseDetails]);
      // Resetting the course details, including the image
      setCourseDetails({ name: '', price: '', description: '', image: '' });
      setIsModalOpen(false);
    } else {
      alert("Please upload an image for the course.");
    }
  };

  const uploadButton = (
    <div>
      <PlusOutlined />
      <div style={{ marginTop: 8 }}>Upload</div>
    </div>
  );

  const updateCourseDetail = (key, value) => {
    setCourseDetails({ ...courseDetails, [key]: value });
  };

  // Function to handle file changes for the image upload
  const handleImageChange = info => {
    if (info.file.status === 'done' || info.file.status === 'removed') {
      getBase64(info.file.originFileObj).then((image) =>
        updateCourseDetail('image', image)
      );
    }
  };

  return (
    <>
      <div className='button'>
        <Button
          style={{ padding: "20px", display: "flex", alignItems: "center", background: "#FEC200" }}
          type="primary"
          onClick={() => setIsModalOpen(true)}
        >
          + Add Course
        </Button>

        <Modal
          title="Add Course"
          style={{ lineHeight: "30px" }}
          open={isModalOpen}
          onOk={handleOk}
          onCancel={() => setIsModalOpen(false)}
        >
          <Upload
            listType="picture-card"
            showUploadList={false}
            beforeUpload={() => false}
            onChange={handleImageChange}
          >
            {courseDetails.image ? <img src={courseDetails.image} alt="course" style={{ width: '100%' }} /> : uploadButton}
          </Upload>

          <Input
            style={{ padding: "10px" }}
            placeholder='Course Name'
            value={courseDetails.name}
            onChange={(e) => updateCourseDetail('name', e.target.value)}
          />
          <Input
            style={{ padding: "10px", margin: "10px 0" }}
            placeholder='Price'
            value={courseDetails.price}
            onChange={(e) => updateCourseDetail('price', e.target.value)}
          />
          <Input
            style={{ padding: "10px" }}
            placeholder='Description'
            value={courseDetails.description}
            onChange={(e) => updateCourseDetail('description', e.target.value)}
          />
        </Modal>
      </div>

      <div style={{ marginTop: '20px' }}>
        {courseList.map((course, index) => (
          <Card
            key={index}
            hoverable
            style={{ width: 240, marginTop: 16 }}
            cover={<img alt="Course" src={course.image} style={{ maxWidth: '100%', maxHeight: '200px' }} />}
          >
            <Card.Meta title={course.name} description={`Price: $${course.price}`} />
            <div style={{ marginTop: '10px' }}>{course.description}</div>
          </Card>
        ))}
      </div>
    </>
  );
};

export default App;
