import React, {useEffect} from 'react';
import AddPostModal from "./addPostModal";
import {Button, Switch, Table, message, Popconfirm } from 'antd';
import {useSelector,useDispatch} from "react-redux";
import {getAnnouncements, deleteAnnouncement, editAnnouncements} from "../../../redux/actions/app";
import {DeleteOutlined, EditOutlined} from "@ant-design/icons";
import {updateState} from "../../../redux/reducers";

function PostModal(props) {
    const {announcements, isModal} = useSelector(state => state.app);
    const dispatch = useDispatch();

    useEffect(()=>{
        dispatch(getAnnouncements());
    },[]);

    const confirm = (e) => {
        dispatch(deleteAnnouncement(e.id));
    };

    const columns = [
        {
            title:'N',
            render:(a,b,index) => index+1
        },
        {
            title: 'Post nomi',
            render:(item)=> <div className={'courseImg'}>
                <img src={item?.image} alt="no-img"/>
                <span>{item?.title}</span>
            </div>
        },
        {
            title: 'Post haqida malumot',
            dataIndex: 'description',
        },
        {
            title: 'Holat',
            render:(e)=> <Switch onChange={(status)=> dispatch(editAnnouncements({...e,status})) } checked={e?.status} />
        },
        {
            align: 'center',
            render:(e)=> <div className={'actions'}>
                <Button onClick={()=>dispatch(updateState({isModal:true, editData:e}))} style={{padding: 0}} type={'primary'} > <EditOutlined /> </Button>
                <Popconfirm
                    title="Delete the task"
                    description="Are you sure to delete this task?"
                    onConfirm={()=>confirm(e)}
                    okText="Yes"
                    cancelText="No"
                    placement="leftTop"
                >
                <Button  style={{padding: 0}}  type={'primary'} danger > <DeleteOutlined /> </Button>
                </Popconfirm>
                </div>
        }
    ]
    
    return (
        <div>
            <div className="Header">
                <h2 className={'titleColor'}> Post </h2>
                <div className="actions">
                    <AddPostModal/>
                </div>
            </div>
            <Table  columns={columns} dataSource={announcements} > </Table>
        </div>
    );
}

export default PostModal;
