import React, {useEffect} from 'react';
import {Button, Drawer, Form, Input} from "antd";
import {useSelector,useDispatch} from "react-redux";
import {updateState} from "../../../redux/reducers";
import {addWebinars, editWebinar} from "../../../redux/actions/app";

function AddModal() {
    const {isModal,isLoading,editData} = useSelector(state => state.app);
    const dispatch = useDispatch();
    const [form] = Form.useForm()

    const toggleModal = () => dispatch(updateState({isModal: !isModal}));

    const onFinish = value => {
        dispatch(updateState({isLoading:true}))
        if (editData?.id){
            dispatch(editWebinar({...value, id:editData.id }))
        } else {
            dispatch(addWebinars(value));
        }
    }
    useEffect(()=>{
        if(editData?.id){
            form.setFieldsValue(editData);
        }
        if (!isModal){
            dispatch(updateState({editData:{}}))
            form.resetFields();
        }
    },[isModal])



    return (
        <div>
            <Button onClick={toggleModal} className={'addBtn'}> Webinar yuklash </Button>
            <Drawer open={isModal} onClose={toggleModal} extra={ <Button onClick={()=> form.submit() } loading={isLoading} className={'addBtn'}> Saqlash </Button> }>
                <Form form={form} name={'webinar'} onFinish={onFinish} layout={"vertical"}>
                    <Form.Item name={'title'} label={'Webinar nomi'} rules={[{required:true}]}>
                        <Input/>
                    </Form.Item>
                    <Form.Item name={'link'} label={'Webinar youtube havolasi'} rules={[{required:true}]}>
                        <Input/>
                    </Form.Item>
                    <Form.Item name={'description'} label={'Webinar haqida malumot'} rules={[{required:true}]}>
                        <Input.TextArea/>
                    </Form.Item>
                </Form>
            </Drawer>
        </div>
    );
}

export default AddModal;
