import React, { useState, useEffect } from 'react';
import { Card, Input, Button } from 'antd';
import 'antd/dist/antd.css'; 

const { TextArea } = Input;

const VideoUploadPage = () => {
  const [videos, setVideos] = useState(
    JSON.parse(localStorage.getItem('videos')) || []
  );
  const [videoInfo, setVideoInfo] = useState({
    title: '',
    description: '',
    url: ''
  });

  useEffect(() => {
    localStorage.setItem('videos', JSON.stringify(videos));
  }, [videos]);

  // Handle the changes in the input fields
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setVideoInfo({ ...videoInfo, [name]: value });
  };

  // Handle the video upload action
  const addVideo = () => {
    if (!videoInfo.title || !videoInfo.description || !videoInfo.url) {
      alert("Please fill in all fields.");
      return;
    }
    const newVideos = [...videos, videoInfo];
    setVideos(newVideos);
    setVideoInfo({ title: '', description: '', url: '' }); // Reset for next entry
    
    // No need to call `localStorage.setItem` here because the `useEffect` will handle it
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Upload Video Information</h2>
      <Input
        placeholder="Video Title"
        style={{ marginBottom: '10px' }}
        value={videoInfo.title}
        name="title"
        onChange={handleInputChange}
      />
      <TextArea
        placeholder="Video Description"
        rows={4}
        style={{ marginBottom: '10px' }}
        value={videoInfo.description}
        name="description"
        onChange={handleInputChange}
      />
      <Input
        placeholder="Video URL"
        style={{ marginBottom: '10px' }}
        value={videoInfo.url}
        name="url"
        onChange={handleInputChange}
      />
      <Button type="primary" onClick={addVideo}>
        Upload Video
      </Button>
      <div style={{ marginTop: '20px' }}>
        {videos.map((video, index) => (
          <Card key={index} title={video.title} style={{ marginBottom: '10px' }}>
            <p>{video.description}</p>
            <video width="100%" controls src={video.url} />
          </Card>
        ))}
      </div>
    </div>
  );
};

export default VideoUploadPage;
